    private void MonthlySalesActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_MonthlySalesActionPerformed
        // TODO add your handling code here:
        String selectedMonth = (String) month.getSelectedItem();
        String year = yearField.getText();
    
    if (selectedMonth != null && !selectedMonth.equals("--Select Month--") && !year.isEmpty()) {
        String report = MonthlySales(selectedMonth, year);
        TextArea.setText(report);  // Display report in JTextArea
    } else {
        TextArea.setText("Please select a month and enter a year.");
    }
    }//GEN-LAST:event_MonthlySalesActionPerformed

