    private String MonthlySales(String month, String year) {
         // Dummy data for demonstration
    double totalRevenue = 1200.50;  
    int numberOfTransactions = 45;  
    
    return "Monthly Sales Report for " + month + " " + year + "\n" +
           "Total Revenue: $" + totalRevenue + "\n" +
           "Number of Transactions: " + numberOfTransactions;
    }

    private String ProductWise() {
        // Dummy data for demonstration
    StringBuilder report = new StringBuilder();
    report.append("Product-Wise Report\n");
    report.append("Product A: 150 units sold\n");
    report.append("Product B: 75 units sold\n");
    report.append("Product C: 200 units sold\n");
    
    return report.toString();
    }
